import "core-js/modules/es.array.push.js";
import ProductDataService from '@/services/ProductDataService';
export default {
  name: 'App',
  data() {
    return {
      inventory: []
    };
  },
  methods: {
    addProduct(product) {
      ProductDataService.create(product).then(response => {
        this.inventory.push(response.data);
      });
    },
    updateProduct(product) {
      ProductDataService.update(product.id, product).then(response => {
        console.log(response.data);
      });
    },
    deleteProduct(id) {
      ProductDataService.delete(id).then(response => {
        console.log(response.data);
      });
    }
  },
  mounted() {
    ProductDataService.getAll().then(response => {
      this.inventory = response.data;
    });
  }
};
